mapper.update:
 		UPDATE MEMBER_BLOCK SET
		BLOCK_END = TO_DATE(#{blockEnd},'YYYY-MM-DD HH24:MI:SS')
		WHERE MEMBER_NO = #{memberNo}

mapper.select:
SELECT * FROM ( SELECT FONT_NO, FONT_NAME ,FONT_PATH ,FONT_CREATER , NVL(COUNT_NO,0) FONT_USE_COUNT FROM FONT_SHOP F LEFT JOIN ( SELECT COUNT(*) COUNT_NO, GOODS_NO FROM MEMBER_OWN_GOODS WHERE SHOP_CATH_NO = 1 GROUP BY GOODS_NO ORDER BY COUNT_NO DESC ) S ON (F.FONT_NO = S.GOODS_NO))
WHERE FONT_NAME LIKE '%${fontName}%'
ORDER BY ${order}
