mapper.update:
UPDATE "MEMBER" M SET
MEMBER_PW = #{encPw}
WHERE MEMBER_EMAIL = #{result}
AND (SELECT COUNT(*) FROM MEMBER_BLOCK MB WHERE MB.MEMBER_NO = M.MEMBER_NO) = 0
AND MEMBER_DEL_YN = 'N'

 	UPDATE "MEMBER" SET
