	MEMBER_TEL= #{memberTel},
	MEMBER_NAME = #{memberName}

		,BIRTHYEAR = #{birthYear},
		BIRTHDAY = #{birthDay}

	WHERE MEMBER_NO = #{memberNo}

mapper.select:
SELECT MEMBER_EMAIL, MEMBER_PW, MEMBER_NAME
FROM "MEMBER"
WHERE MEMBER_NO = #{memberNo}
