mapper.update:
UPDATE CATEGORY_ORDER SET

	DIARY = #{diary},


	ALBUM = #{album},


	VIDEO = #{video},


	GUEST_BOOK = #{guestBook}

WHERE MEMBER_NO = #{memberNo}

