mapper.delete:
DELETE FROM FOLDER 
WHERE MEMBER_NO = #{memberNo}
AND FOLDER_NO = #{folderNo}

mapper.update:
UPDATE FOLDER SET
FOLDER_ORDER = #{folderNewOrder}
WHERE MEMBER_NO = #{memberNo}
AND FOLDER_ORDER = #{folderNewOrder} + 1
AND BOARD_TYPE_NO = #{boardTypeNo}

UPDATE FOLDER SET
FOLDER_ORDER = #{folderOrder}
WHERE MEMBER_NO = #{memberNo}
AND FOLDER_NO = #{folderNo}
AND BOARD_TYPE_NO = #{boardTypeNo}
