mapper.select:
SELECT COUNT(*)

	FROM VIDEO
	WHERE FOLDER_NO = #{folderNo}
	AND V_OPEN_FL <= #{flag}
	AND V_DEL_YN = 'N'

mapper.update:
UPDATE VIDEO SET
VIDEO_PATH = #{videoPath},
VIDEO_ORIGINAL_NAME = #{videoOriginalName},
VIDEO_RENAME = #{videoRename},
V_THUMNAIL = #{videoThumbnail}
WHERE VIDEO_NO = #{videoNo}

mapper.delete:
UPDATE VIDEO SET
V_DEL_YN = 'Y'
WHERE VIDEO_NO = #{videoNo}
